 *{
    text-shadow:none !important;
    font-family: 'Lora',Georgia, Garamond, Times, nanummyeongjo, 'Nanum Myeongjo', Batang, Gungseo, serif;
    box-shadow:none !important
}
div#container-wrap{
    background:rgb(240,240,240);
}
a{
    color:#903000
}
a:hover{
    color:#B06030;
    text-decoration:none
}
div#navi-bar,div#navi-bar-shadow{
    display:none
}
#header{
    width:100%;
    max-width:100vw;
    margin:0;
    padding:0;
    position:relative;
    background:#fff;
}
#header h1 a span{
    color:#333;
    font-size:24pt;
    font-family: 'Lora',nanumsquare, nanumbarungothic, 'Nanum Barun Gothic', Helvetica, sans-serif
}
#header h1{
    height:72pt;
    padding:0;
    display:block;
    margin:0;
    text-align:center;
    width:100%;
    max-width:100%;
    overflow:hidden
}
#header h1 a{
    margin:0;
    width:100%;
    padding:0;
    line-height:72pt;
    font-size:18pt
}
#header h1 a span:hover{
    color:#666
}
#header h2{
    display:none
}
#search-top-box-input{
    border-color:#999;
    color:transparent;
    background:#fff
}
#search-top-box-input:focus,#search-top-box-input:hover{
    border-color:#999;
    color:#333;
    background:#ffe
}
#search-top-box-form input[type=submit]{
    background:0 0;
    color:#333
}
#search-top-box-form input[type=submit]:focus,#search-top-box-form input[type=submit]:hover{
    background:#666;
    color:#fff
}
div#top-bar{
    display:block;
    top:72pt;
    position:absolute;
    left:0;
    right:0;
    max-width:100%;
    width:21cm;
    padding:0;
    background:transparent;
    margin:0 auto
}
#top-bar a{
    color:#333 !important;
    background:none !important;
    border:none !important
}
#top-bar ul{
    width:21cm;
    max-width:100vw;
    text-align:center;
    border:none;
    z-index:51
}
#top-bar ul > li{
    display:inline-block;
    float:none;
    border:none
}
#top-bar ul>li>a{
    color:#333;
    border:none
}
#top-bar ul>li.sfhover>a,#top-bar ul>li:hover>a{
    color:#333
}
#top-bar ul li ul{
    background:#fff;
    overflow:hidden;
    width:10em
}
#top-bar ul li ul li{
    display:inline-block;
    width:100%;
    overflow:hidden;
    white-space:nowrap;
    text-overflow:ellipsis;
    border-bottom:1px solid #eee;
    text-align:left
}
#top-bar ul li ul li.sfhover,#top-bar ul li ul li:hover{
    background:#666
}
#top-bar ul li ul li a,#top-bar * ul li ul li a{
    color:#333;
    background:0 0;
    display:inline;
    border:none
}
#top-bar ul li ul li:hover a{
    color:#eee;
    background:0 0
}
div#side-bar{
    position:fixed;
    left:0
}
.open-menu{
    display:none
}
div#side-bar{
    position:fixed;
    margin-left:-20em;
    height:100%;
    overflow-y:auto;
    overflow-x:hidden;
    top:0;
    background:#333;
    width:17em;
    display:block !important;
    color:#eee;
    z-index:10;
    transition-duration:1s
}
div#side-bar:before{
    position:fixed;
    height:90px;
    width:90px;
    border:1px solid rgba(3,3,3,0.5);
    content:'>';
    font-size:20px;
    text-align:center;
    text-indent:33px;
    line-height:90px;
    top:50%;
    margin-top:-45px;
    left:-45px;
    color:rgba(3,3,3,0.5);
    border-radius:45px;
    transition-duration:1s
}
div#side-bar:hover{
    position:fixed;
    margin-left:0;
    left:0 !important
}
div#side-bar:hover:before{
    transition-delay:1s;
    background:#333;
    color:#eee;
    border-color:#eee;
    margin-left:-900px
}
#side-bar .side-block{
    border-radius:unset;
    border:none;
    background:#333 !important;
    margin:0
}
#side-bar .heading,#side-bar .collapsible-block-unfolded-link{
    color:#fff;
    font-weight:bold;
    font-size:11pt;
    border-bottom:1px solid #eee;
    text-align:center;
    margin:1.5em 0 0;
    padding:0
}
#side-bar .collapsible-block-unfolded-link a.collapsible-block-link{
    color:#fff;
    font-weight:bold;
    font-size:11pt
}
#side-bar .menu-item{
    border-bottom:1px solid #666;
    line-height:2em
}
#side-bar a{
    color:#eee
}
#side-bar .sub-text{
    display:none
}
#side-bar .menu-item img{
    display:none
}
#side-bar>hr{
    display:none
}
div#main-content{
    margin:0 auto !important;
    width:545px;
    max-width:100%;
        background:rgb(255,255,255);
padding:20px;
text-align:justify;
}
@media screen and (max-width:737px){
    div#main-content{
        padding:0 10px !important;
        box-sizing:border-box
    }
}
#page-title,#page-title *{
    font-size:20pt;
    font-weight:bold;
    font-family: 'Lora',Georgia, Garamond, Times, nanummyeongjo, 'Nanum Myeongjo', Batang, Gungseo, 'Nanum Myeongjo', Batang, serif
}
#page-title{
    border:none;
    color:#333;
    margin-bottom:2em;
    text-align:center
}
.page-rate-widget-box{
    background:transparent;
    padding:0;
    display:none
}
.page-rate-widget-box .rate-points{
    background-color:transparent !important;
    color:#666;
    border:3px solid #eee;
    border-right:1px solid #eee;
    font-weight:normal
}
.page-rate-widget-box span.rateup a,.page-rate-widget-box .ratedown a{
    color:#333
}
.page-rate-widget-box span.rateup,.page-rate-widget-box span.ratedown{
    background-color:transparent;
    border-top:3px solid #eee;
    border-bottom:3px solid #eee
}
.page-rate-widget-box span.cancel{
    border:3px solid #eee;
    border-left:1px solid #eee;
    background:transparent
}
.page-rate-widget-box .cancel a{
    color:#666
}
#page-content,#page-content *,#page-content .yui-navset li,#page-content a,#page-content div,#page-content em,#page-content li,#page-content p,textarea{
    font-family: 'Lora',Georgia, Garamond, Times, nanummyeongjo, 'Nanum Myeongjo', Batang, Gungseo, serif;
}
#page-content,#page-content .yui-navset li,#page-content em,#page-content li,#page-content p,textarea{
    line-height:1.8em;
    letter-spacing:-0.015em;
    font-size:11pt;
    font-weight:400;
    color:#333
}
#page-content b,#page-content strong{
    font-weight:700
}
#page-content hr{
    padding:0;
    border-top:1px solid #333;
    border:none;
    color:#333;
    text-align:center
}
#page-content blockquote{
    background:transparent
}
.feature-block .panel-body > p{
    display:none
}
.international-entry,.international-entry:nth-child(2n){
    border:none;
    width:unset;
    background:transparent
}
.international-title{
    width:unset
}
.international-title :not(a){
    display:none
}
.international-description,.international-footer{
    display:none
}
.info-subtitle{
    display:none
}
.content-panel{
    border:3px solid #eee;
    border-radius:unset;
    background:transparent
}
.content-panel .panel-heading{
    background:none;
    color:#333;
    text-align:center
}
#page-content .content-panel .panel-heading p{
    font-weight:bold
}
.content-panel .panel-body{
    background:0 0;
    border-left:1px solid #eee;
    border-right:1px solid #eee
}
.content-panel .panel-footer{
    background:none;
    color:#999
}
.content-panel .panel-footer a{
    color:#999
}
div.yui-navset ul.yui-nav,div.yui-navset-top ul.yui-nav{
    border:none;
    opacity:.5;
    transition-duration:1s;
    background:#fff
}
div.yui-navset ul.yui-nav:hover,div.yui-navset-top ul.yui-nav:hover{
    opacity:1
}
div.yui-navset ul.yui-nav li,div.yui-navset-top ul.yui-nav li{
    display:inline-block;
    border:solid #ddd;
    border-width:0 0 1px;
    list-style:circle;
    transition-duration:.3s;
    white-space:nowrap;
    text-overflow:ellipsis;
    overflow:hidden
}
div.yui-navset ul.yui-nav li a em,div.yui-navset ul.yui-nav li:last-child,div.yui-navset-top ul.yui-nav li a em,div.yui-navset-top ul.yui-nav li:last-child{
    border:none
}
div.yui-navset ul.yui-nav li.selected,div.yui-navset-top ul.yui-nav li.selected div.yui-navset ul.yui-nav li.selected:hover,div.yui-navset-top ul.yui-nav li.selected:hover{
    background:#666
}
div.yui-navset ul.yui-nav li:hover,div.yui-navset-top ul.yui-nav li:hover{
    background:#eee;
    color:#fff
}
div.yui-navset ul.yui-nav li a,div.yui-navset-top ul.yui-nav li a{
    display:inline;
    border:none;
    background:0 0;
    color:#333;
    max-width:100%
}
.thread-container .post .long .head .title,.thread-container .post .long .head .title a{
    color:#666;
    font-weight:700;
    font-family: 'Lora',Georgia, Garamond, 'Adobe Garamond', Nanummyeongjo, 'Nanum Myeongjo', serif !important
}
.thread-container .post{
    background:0 0;
    padding:.5em 0;
    border-bottom:1px dotted #999
}
.thread-container .post .head{
    box-shadow:none;
    background:0 0;
    padding:.5em 2em .25em
}
.thread-container .post .long .head .title{
    font-size:12pt
}
.thread-container .post .long .head .title a{
    text-decoration:none
}
.thread-container .post .long .head .title a:hover{
    color:#901
}
.thread-container .post .long .head .title a:empty:before{
    content:'Non-titled Post';
    color:#666
}
.thread-container .post .long .head .info{
    text-align:right;
    color:#666
}
.thread-container .post .long .head .info .printuser a{
    color:#666
}
.thread-container .post .long .head .info .printuser img{
    display:none
}
.thread-container .post .long .head .info *{
    color:#999
}
.thread-container .post .long .content{
    padding:.25em 2em .5em
}
div#footer{
    display:none !important
}
