    #mw-navigation {
        position: absolute !important;
        top: 0 !important;
        left: 0 !important;
        right: 0 !important;
    }
    #mw-head {
        position: relative !important;
        max-width: 870px !important;
        height: 90px !important;
        margin: 0 auto !important;
    }
    #mw-head a,
    #mw-head ul,
    #mw-head li,
